	/**
	 * Get allowed HTML tags including SVG elements.
	 *
	 * @return array Array of allowed HTML tags and attributes.
	 */
	private function get_allowed_svg() {
		$kses_defaults = wp_kses_allowed_html( 'post' );
		
		$svg_args = array(
			'svg'     => array(
				'class'           => true,
				'xmlns'           => true,
				'width'           => true,
				'height'          => true,
				'viewbox'         => true,
				'fill'            => true,
				'aria-hidden'     => true,
				'focusable'       => true,
			),
			'g'       => array(
				'fill'            => true,
				'fill-rule'       => true,
			),
			'path'    => array(
				'd'               => true,
				'fill'            => true,
				'fill-rule'       => true,
				'stroke'          => true,
				'stroke-width'    => true,
				'stroke-linecap'  => true,
				'stroke-linejoin' => true,
			),
			'circle'  => array(
				'cx'              => true,
				'cy'              => true,
				'r'               => true,
				'fill'            => true,
				'stroke'          => true,
				'stroke-width'    => true,
			),
			'rect'    => array(
				'x'               => true,
				'y'               => true,
				'width'           => true,
				'height'          => true,
				'fill'            => true,
				'stroke'          => true,
				'stroke-width'    => true,
				'rx'              => true,
				'ry'              => true,
			),
			'polygon' => array(
				'points'          => true,
				'fill'            => true,
				'fill-rule'       => true,
			),
		);
		
		return array_merge($kses_defaults, $svg_args);
	}

	/**
	 * Get block data (title, icon, description).
	 *
	 * @param string $block_name Block name.
	 * @return array|false Block data or false if not found.
	 */
	public function get_block_data( $block_name = '' ) {
		// Define all block data
		$blocks_data = array(
			'container' => array(
				'title'       => __( 'Container', 'digiblocks' ),
