	private function has_lottie_blocks($blocks) {
		foreach ($blocks as $block) {
			if (isset($block['blockName']) && $block['blockName'] === 'digiblocks/lottie') {
				return true;
			}
			
			if (!empty($block['innerBlocks'])) {
				if ($this->has_lottie_blocks($block['innerBlocks'])) {
					return true;
				}
			}
		}
		
		return false;
	}

